const areaRef=document.querySelector(".video-section__player"),btnRef=areaRef.querySelector(".play-btn"),cursorCords={x:-1,y:-1},checkPeriod=50;function setCursorCords(e){cursorCords.x=e.pageX,cursorCords.y=e.pageY}function moveBtn(e){var t=areaRef.getBoundingClientRect(),o=t.left+window.pageXOffset,r=t.top+window.pageYOffset,o=cursorCords.x-o,r=cursorCords.y-r;if(o>t.width||r>t.height||o<0||r<0)return btnRef.style.top="50%",void(btnRef.style.left="50%");btnRef.style.top=r+"px",btnRef.style.left=o+"px"}document.addEventListener("mousemove",setCursorCords),setInterval(moveBtn,checkPeriod);